import { configureStore, createSlice } from "@reduxjs/toolkit";


const cartSlice = createSlice({
    name: "cart",
    initialState: {
        isShowing: true,
        items: [{ title: "test", quantity: 3, total: 18, price: 6 }],
    },
    reducers: {
        toggleShow: (state) => {
            state.isShowing = !state.isShowing;
        },
        addItem: (state, action) => {
            const item = action.payload;
            const indexOfItem = state.items.findIndex(i => i.title === item.title);
            if (indexOfItem === -1) {
                state.items.push({
                    title: item.title,
                    quantity: item.quantity,
                    price: item.price,
                    total: item.price * item.quantity,
                });
            } else {
                state.items[indexOfItem].quantity += item.quantity;
                state.items[indexOfItem].total = state.items[indexOfItem].quantity * state.items[item.title].price;
            }
        }
    }
})


export const store = configureStore({reducer: cartSlice.reducer});


export const cartActions = cartSlice.actions;
