import CartContext from "./CartContext";

const defaultCartState = {
    items:[],
    totalAmount: 0,
}

const cartReducer = (state, action) => {
    if (action.type === 'ADD_ITEM') {
        return {
            items: state.items.append(action.item),
            totalAmount: state.totalAmount + action.item.totalAmount,
        }
    }
}

