import { useState } from "react";



const useHttp = (link) => {
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState('');

    const sendRequest = async (applyData, requestConfig) => {
        setIsLoading(true);
        
        try {
            let data;
            if (requestConfig) {
                data = await fetch("https://react-guide-8c237-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json", requestConfig);
            } else {
                data = await fetch("https://react-guide-8c237-default-rtdb.asia-southeast1.firebasedatabase.app/meals.json");
            }

            data = await data.json();

            applyData(data);

        } catch(e) {
            setError(e.toString());
        }

        setIsLoading(false);
    }
    
    return {isLoading, error, sendRequest};
};

export default useHttp;
