import { useEffect, useState } from "react";

const SimpleInput = (props) => {

  const [enteredName, setEnteredName] = useState("");
  const [nameIsTouched, setNameIsTouched] = useState(false);

  const nameIsValid = enteredName.trim() !== "";

  const [enteredEmail, setEnteredEmail] = useState("");
  const [emailIsTouched, setEmailIsTouched] = useState(false);

  const emailIsValid = enteredEmail.trim() !== "" && enteredEmail.includes("@"); 

  let formIsValid = nameIsValid && emailIsValid;

  const nameInputHandler = event => {
    setEnteredName(event.target.value);
  }

  const formSubmissionHandler = event => {
    event.preventDefault();
    setNameIsTouched(true)

    if (!nameIsValid) {
      return;
    }

    setEnteredName("");
    setNameIsTouched(false);
  }

  const blurNameInputHandler = () => {
    console.log("yes");
    setNameIsTouched(true);
  }


  const emailInputHandler = (event) => {
    setEnteredEmail(event.target.value);
  }


  const nameIsInvalidAndTouched = nameIsTouched && !nameIsValid; 
  const emailIsInvalidAndTouched = emailIsTouched && !emailIsValid;

  const inputCSS = nameIsInvalidAndTouched ? "form-control invalid" : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={inputCSS}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameInputHandler} value={enteredName} onBlur={blurNameInputHandler}/>
        <input type='text' id='email' onChange={emailInputHandler} value={enteredEmail} onBlur={blurEmailInputHandler}/>
        {nameIsInvalidAndTouched ?? <p>Names should not be empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
