import { useEffect, useState } from "react";

const SimpleInput = (props) => {

  const [enteredName, setEnteredName] = useState("");
  const [nameIsTouched, setNameIsTouched] = useState(false);

  const nameIsValid = enteredName.trim() !== "";

  const [formIsValid, setFormIsValid] = useState(false);

  useEffect(() => {
    setFormIsValid(nameIsValid && anyOtherFormInputs);
  }, [nameIsValid]);

  const nameInputHandler = event => {
    setEnteredName(event.target.value);
  }

  const formSubmissionHandler = event => {
    event.preventDefault();
    setNameIsTouched(true)

    if (!nameIsValid) {
      return;
    }

    console.log(enteredName);
    setEnteredName("");
    setNameIsTouched(false);
  }

  const blurInputHandler = () => {
    console.log("yes");
    setNameIsTouched(true);
  }

  const nameIsInvalidAndTouched = nameIsTouched && !nameIsValid; 

  const inputCSS = nameIsInvalidAndTouched ? "form-control invalid" : "form-control";

  return (
    <form onSubmit={formSubmissionHandler}>
      <div className={inputCSS}>
        <label htmlFor='name'>Your Name</label>
        <input type='text' id='name' onChange={nameInputHandler} value={enteredName} onBlur={blurInputHandler} onFocus={blurInputHandler}/>
        {nameIsInvalidAndTouched ?? <p>Names should not be empty</p>}
      </div>
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
