import ChartBar from "./ChartBar";

const Chart = props => {


    const totalMaximum = Math.max(...props.dataPoints.map(data => data.value));

    return(
        <div className="chart">
            {
                props.dataPoints.map(
                    dataPoint => {
                        return <ChartBar key={dataPoint.label} data={dataPoint.data} maxValue={totalMaximum} label={dataPoint.label}/>
                    }
                )
            }
        </div>
    )
}

export default Chart;
