import React, { useEffect, useState } from 'react';

import Login from './components/Login/Login';
import Home from './components/Home/Home';
import MainHeader from './components/MainHeader/MainHeader';
import AuthContext from './components/Context/auth-context';

function App() {

  return (
    <React.Fragment>
      <MainHeader isAuthenticated={AuthContext.isLoggedIn} onLogout={AuthContext.logoutHandler} />
      <main>
        {!AuthContext.isLoggedIn && <Login onLogin={AuthContext.loginHandler} />}
        {AuthContext.isLoggedIn && <Home onLogout={AuthContext.logoutHandler} />}
      </main>
    </React.Fragment>
  );
}

export default App;
