import React, { useEffect, useReducer, useState } from 'react';

import Card from '../UI/Card/Card';
import classes from './Login.module.css';
import Button from '../UI/Button/Button';


const emailReducer = (state, action) => {

  if (action.type === 'USER_INPUT') {
    return {
      value: action.value,
      isValid: action.value.include('@'),
    }
  } else if (action.type === 'INPUT_BLUR') {
    return {
      value: state.value,
      isValid: action.value.include('@'),
    }
  }

  return {
    value:'',
    isValid: false,
  }
};

const passwordReducer = (state, action) => {
  if (action.type === 'USER_INPUT') {
    return {
      val: action.val,
      isValid: action.val.trim().length > 6,
    }
  } else if (action.type === 'INPUT_BLUR') {
    return {
      val: state.val,
      isValid: action.val.trim().length > 6
    }
  }
}


const Login = (props) => {
  const [formIsValid, setFormIsValid] = useState(false);

  const [emailState, dispatchEmail] = useReducer(emailReducer, { value: '', isValid: false,})
  const [passwordState, dispatchPassword] = useReducer(passwordReducer, { value: '', isValid: false });


  // useEffect(() => {

  //   const identifer = setTimeout(() => {
  //     setFormIsValid(
  //       enteredEmail.trim().length > 6 && enteredEmail.includes('@') && enteredPassword.includes('@') && enteredPassword.trim().length > 6
  //     );
  //   }, 500)

  //   return () => {
  //     clearTimeout(identifer);
  //   }
  // }, [enteredEmail, enteredPassword])

  const emailChangeHandler = (event) => {

    dispatchEmail({type: 'USER_INPUT', val: event.target.value})

    setFormIsValid(
            passwordState.val.trim().length > 6 && emailState.value.includes('@')
    );
  };

  const passwordChangeHandler = (event) => {
    dispatchPassword({type: 'USER_INPUT', val: event.target.value,});
    setFormIsValid(
      emailState.value.includes('@') && passwordState.val.trim().length > 6
    );
  };

  const validateEmailHandler = () => {
    dispatchEmail({type: 'INPUT_BLUR'});
  };

  const validatePasswordHandler = () => {
    dispatchPassword({type: 'INPUT_BLUR'});
  };

  const submitHandler = (event) => {
    event.preventDefault();
    props.onLogin(emailState.value, passwordState.val);
  };

  return (
    <Card className={classes.login}>
      <form onSubmit={submitHandler}>
        <div
          className={`${classes.control} ${
            emailState.isValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="email">E-Mail</label>
          <input
            type="email"
            id="email"
            value={emailState.value}
            onChange={emailChangeHandler}
            onBlur={validateEmailHandler}
          />
        </div>
        <div
          className={`${classes.control} ${
            passwordState.isValid === false ? classes.invalid : ''
          }`}
        >
          <label htmlFor="password">Password</label>
          <input
            type="password"
            id="password"
            value={passwordState.val}
            onChange={passwordChangeHandler}
            onBlur={validatePasswordHandler}
          />
        </div>
        <div className={classes.actions}>
          <Button type="submit" className={classes.btn} disabled={!formIsValid}>
            Login
          </Button>
        </div>
      </form>
    </Card>
  );
};

export default Login;
